{
  "path": "/home/claude/ai-swarm-orchestrator/types/index.ts",
  "file_text": "// Core AI Model Types
export type AIProvider = 'openai' | 'anthropic' | 'google' | 'custom' | 'local';
export type ModelTier = 'free' | 'byok' | 'custom';

export interface AIModel {
  id: string;
  provider: AIProvider;
  name: string;
  displayName: string;
  tier: ModelTier;
  contextWindow: number;
  capabilities: ModelCapability[];
  costPerToken?: {
    input: number;
    output: number;
  };
}

export type ModelCapability = 
  | 'code_generation' 
  | 'code_review' 
  | 'planning' 
  | 'documentation' 
  | 'testing' 
  | 'debugging'
  | 'architecture'
  | 'database_design'
  | 'ui_design';

// Agent Types
export interface Agent {
  id: string;
  name: string;
  type: AgentType;
  description: string;
  model: AIModel;
  systemPrompt: string;
  capabilities: ModelCapability[];
  config: AgentConfig;
  status: AgentStatus;
  metrics?: AgentMetrics;
}

export type AgentType = 
  | 'planner'
  | 'architect'
  | 'frontend_dev'
  | 'backend_dev'
  | 'fullstack_dev'
  | 'database_admin'
  | 'devops'
  | 'code_reviewer'
  | 'qa_tester'
  | 'tech_writer'
  | 'ui_designer'
  | 'data_scientist'
  | 'security_expert'
  | 'custom';

export interface AgentConfig {
  temperature: number;
  maxTokens: number;
  topP: number;
  frequencyPenalty: number;
  presencePenalty: number;
  apiKey?: string;
  customEndpoint?: string;
  retryConfig: {
    maxRetries: number;
    backoffMs: number;
  };
}

export type AgentStatus = 'idle' | 'active' | 'busy' | 'error' | 'paused';

export interface AgentMetrics {
  tasksCompleted: number;
  tokensUsed: number;
  averageResponseTime: number;
  errorRate: number;
  lastActive: Date;
}

// Workflow Types
export interface Workflow {
  id: string;
  name: string;
  description: string;
  repositoryUrl: string;
  nodes: WorkflowNode[];
  edges: WorkflowEdge[];
  variables: Record<string, any>;
  createdAt: Date;
  updatedAt: Date;
  status: WorkflowStatus;
}

export interface WorkflowNode {
  id: string;
  type: 'agent' | 'condition' | 'parallel' | 'sequence' | 'loop';
  position: { x: number; y: number };
  data: WorkflowNodeData;
}

export interface WorkflowNodeData {
  agent?: Agent;
