// === UTILITY FUNCTIONS ===

export function formatTokenCount(tokens: number): string {
  if (tokens < 1000) return `${tokens} tokens`;
  if (tokens < 1000000) return `${(tokens / 1000).toFixed(1)}K tokens`;
  return `${(tokens / 1000000).toFixed(2)}M tokens`;
}

export function estimateCost(tokens: number, model: AIModel): number {
  if (!model.costPerToken) return 0;
  return tokens * (model.costPerToken.input + model.costPerToken.output) / 2;
}

export function formatCurrency(amount: number): string {
  return new Intl.NumberFormat('en-US', {
    style: 'currency',
    currency: 'USD',
    minimumFractionDigits: 4
  }).format(amount);
}

export function calculateWorkflowDuration(start: Date, end?: Date): string {
  const endTime = end || new Date();
  const durationMs = endTime.getTime() - start.getTime();

  const seconds = Math.floor(durationMs / 1000);
  const minutes = Math.floor(seconds / 60);
  const hours = Math.floor(minutes / 60);

  if (hours > 0) return `${hours}h ${minutes % 60}m`;
  if (minutes > 0) return `${minutes}m ${seconds % 60}s`;
  return `${seconds}s`;
}

export function validateAPIKey(key: string, provider: AIProvider): boolean {
  switch (provider) {
    case 'openai':
      return key.startsWith('sk-') && key.length > 20;
    case 'anthropic':
      return key.startsWith('sk-ant-') && key.length > 20;
    case 'google':
      return key.length > 20; // Google keys don't have a standard prefix
    default:
      return key.length > 0;
  }
}

