export const useExecutionLogStore = create<ExecutionLogStore>((set, get) => ({
  events: [],
  maxEvents: 1000,

  addEvent: (event) =>
    set((state) => {
      const newEvents = [...state.events, event];
      // Keep only the most recent events
      if (newEvents.length > state.maxEvents) {
        return { events: newEvents.slice(-state.maxEvents) };
      }
      return { events: newEvents };
    }),

  clearEvents: () => set({ events: [] }),

  filterEventsByWorkflow: (workflowId) => {
    const { events } = get();
    return events.filter((e) => e.workflowId === workflowId);
  },

  filterEventsByAgent: (agentId) => {
    const { events } = get();
    return events.filter((e) => e.agentId === agentId);
  },
}));

