      id: string;
      name: string;
      email: string;
      image?: string;
      accessToken: string;
      refreshToken?: string;
      provider: 'github' | 'google';
    };
  }
}

declare module 'next-auth/jwt' {
  interface JWT {
    id: string;
    accessToken: string;
    refreshToken?: string;
    provider: string;
  }
}
