          role: m.role === 'system' ? 'user' : m.role,
          content: m.content,
        })),
      });

      const content = response.content[0];
      const textContent = content.type === 'text' ? content.text : '';

      const tokensUsed = {
        input: response.usage.input_tokens,
        output: response.usage.output_tokens,
        total: response.usage.input_tokens + response.usage.output_tokens,
      };

      // Calculate cost
      const model = AI_MODELS[params.model];
      const cost = model?.costPerToken
        ? (tokensUsed.input * model.costPerToken.input) +
          (tokensUsed.output * model.costPerToken.output)
        : undefined;

      return {
        content: textContent,
        tokensUsed,
        model: params.model,
        finishReason: response.stop_reason === 'end_turn' ? 'stop' :
                      response.stop_reason === 'max_tokens' ? 'length' : 'error',
        cost,
      };
    } catch (error: any) {
      throw new Error(`Anthropic API error: ${error.message}`);
    }
  }

  async *chatStream(params: ChatParams): AsyncGenerator<ChatStreamChunk> {
    try {
      const stream = await this.client.messages.stream({
        model: params.model,
        max_tokens: params.maxTokens ?? 4096,
        temperature: params.temperature ?? 0.7,
        top_p: params.topP ?? 1,
        system: params.systemPrompt,
        messages: params.messages.map(m => ({
          role: m.role === 'system' ? 'user' : m.role,
          content: m.content,
        })),
      });

      let inputTokens = 0;
      let outputTokens = 0;

      for await (const event of stream) {
        if (event.type === 'content_block_delta') {
          const delta = event.delta;
          if (delta.type === 'text_delta') {
            yield {
              content: delta.text,
              done: false,
            };
          }
        } else if (event.type === 'message_start') {
          inputTokens = event.message.usage.input_tokens;
        } else if (event.type === 'message_delta') {
          outputTokens = event.usage.output_tokens;
        }
      }

      // Stream is done
      yield {
        content: '',
        done: true,
        tokensUsed: {
          input: inputTokens,
          output: outputTokens,
          total: inputTokens + outputTokens,
        },
      };
    } catch (error: any) {
