      );
      const outputTokens = this.countTokens(content);

      const tokensUsed = {
        input: inputTokens,
        output: outputTokens,
        total: inputTokens + outputTokens,
      };

      // Calculate cost
      const modelConfig = AI_MODELS[params.model];
      const cost = modelConfig?.costPerToken
        ? (tokensUsed.input * modelConfig.costPerToken.input) +
          (tokensUsed.output * modelConfig.costPerToken.output)
        : undefined;

      return {
        content,
        tokensUsed,
        model: params.model,
        finishReason: response.candidates?.[0]?.finishReason === 'STOP' ? 'stop' :
                      response.candidates?.[0]?.finishReason === 'MAX_TOKENS' ? 'length' : 'error',
