  }

  async chat(params: ChatParams): Promise<ChatResponse> {
    const messages = params.systemPrompt
      ? [{ role: 'system' as const, content: params.systemPrompt }, ...params.messages]
      : params.messages;

    try {
      const response = await fetch(this.endpoint, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${this.apiKey}`,
        },
        body: JSON.stringify({
          model: params.model,
          messages: messages.map(m => ({
            role: m.role,
            content: m.content,
          })),
          temperature: params.temperature ?? 0.7,
          max_tokens: params.maxTokens ?? 4096,
          top_p: params.topP ?? 1,
          frequency_penalty: params.frequencyPenalty ?? 0,
          presence_penalty: params.presencePenalty ?? 0,
        }),
      });

      if (!response.ok) {
        throw new Error(`Custom API error: ${response.statusText}`);
      }

      const data = await response.json();

      // Try to parse OpenAI-compatible format
      const choice = data.choices?.[0];
      const content = choice?.message?.content || data.content || '';

      const tokensUsed = {
        input: data.usage?.prompt_tokens || this.countTokens(messages.map(m => m.content).join(' ')),
        output: data.usage?.completion_tokens || this.countTokens(content),
        total: data.usage?.total_tokens || 0,
      };

      if (!tokensUsed.total) {
        tokensUsed.total = tokensUsed.input + tokensUsed.output;
      }

      return {
        content,
        tokensUsed,
        model: params.model,
        finishReason: choice?.finish_reason === 'stop' ? 'stop' :
                      choice?.finish_reason === 'length' ? 'length' : 'stop',
      };
    } catch (error: any) {
      throw new Error(`Custom provider error: ${error.message}`);
    }
  }

  async *chatStream(params: ChatParams): AsyncGenerator<ChatStreamChunk> {
    const messages = params.systemPrompt
      ? [{ role: 'system' as const, content: params.systemPrompt }, ...params.messages]
      : params.messages;

    try {
      const response = await fetch(this.endpoint, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${this.apiKey}`,
        },
        body: JSON.stringify({
          model: params.model,
          messages: messages.map(m => ({
            role: m.role,
            content: m.content,
          })),
          temperature: params.temperature ?? 0.7,
          max_tokens: params.maxTokens ?? 4096,
          top_p: params.topP ?? 1,
          stream: true,
        }),
      });

      if (!response.ok) {
        throw new Error(`Custom API streaming error: ${response.statusText}`);
      }

      const reader = response.body?.getReader();
      if (!reader) {
        throw new Error('No response body available');
      }

      const decoder = new TextDecoder();
      let buffer = '';
      let fullContent = '';

      while (true) {
        const { done, value } = await reader.read();

        if (done) {
          break;
        }

        buffer += decoder.decode(value, { stream: true });
        const lines = buffer.split('\n');
        buffer = lines.pop() || '';

        for (const line of lines) {
          if (line.startsWith('data: ')) {
            const data = line.slice(6);

            if (data === '[DONE]') {
              const inputTokens = this.countTokens(messages.map(m => m.content).join(' '));
              const outputTokens = this.countTokens(fullContent);

              yield {
                content: '',
                done: true,
                tokensUsed: {
                  input: inputTokens,
                  output: outputTokens,
                  total: inputTokens + outputTokens,
                },
              };
              return;
            }

            try {
              const parsed = JSON.parse(data);
              const content = parsed.choices?.[0]?.delta?.content || '';

              if (content) {
                fullContent += content;
                yield {
                  content,
                  done: false,
                };
              }
            } catch (e) {
              // Skip invalid JSON
            }
          }
        }
      }
    } catch (error: any) {
      throw new Error(`Custom provider streaming error: ${error.message}`);
    }
  }

  countTokens(text: string): number {
    // Simple approximation
    return Math.ceil(text.length / 4);
  }

  async validateApiKey(): Promise<boolean> {
    try {
      const response = await fetch(this.endpoint, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${this.apiKey}`,
        },
        body: JSON.stringify({
          model: 'default',
          messages: [{ role: 'user', content: 'test' }],
          max_tokens: 5,
        }),
      });

      return response.ok || response.status === 401; // 401 means endpoint exists but key is wrong
    } catch (error) {
      return false;
    }
  }
