            path: file.path,
            mode: '100644' as const,
            type: 'blob' as const,
            sha: blobData.sha,
          };
        })
      );

      // Create new tree
      const { data: treeData } = await this.octokit.git.createTree({
        owner,
        repo,
        base_tree: currentTreeSha,
        tree: blobs,
      });

      // Create new commit
      const { data: newCommitData } = await this.octokit.git.createCommit({
        owner,
        repo,
        message,
        tree: treeData.sha,
        parents: [currentCommitSha],
      });

      // Update reference
      await this.octokit.git.updateRef({
        owner,
        repo,
        ref: `heads/${branch}`,
        sha: newCommitData.sha,
      });

      return newCommitData;
    } catch (error: any) {
      throw new Error(`Failed to create commit: ${error.message}`);
    }
  }

  /**
   * Create a pull request
   */
  async createPullRequest(
    owner: string,
    repo: string,
    title: string,
    head: string,
    base: string = 'main',
    body?: string
  ) {
    try {
      const { data } = await this.octokit.pulls.create({
        owner,
        repo,
        title,
        head,
        base,
        body,
      });

      return data;
    } catch (error: any) {
      throw new Error(`Failed to create pull request: ${error.message}`);
    }
  }

  /**
   * Create a new branch
   */
  async createBranch(owner: string, repo: string, newBranch: string, fromBranch: string = 'main') {
    try {
      // Get the SHA of the base branch
      const { data: refData } = await this.octokit.git.getRef({
        owner,
        repo,
        ref: `heads/${fromBranch}`,
      });

      // Create new branch
      await this.octokit.git.createRef({
        owner,
        repo,
        ref: `refs/heads/${newBranch}`,
        sha: refData.object.sha,
      });

      return { branch: newBranch, sha: refData.object.sha };
    } catch (error: any) {
      throw new Error(`Failed to create branch: ${error.message}`);
    }
  }

  /**
   * Helper: Map GitHub API repository to our RepositoryInfo type
   */
  private mapRepository(repo: any): RepositoryInfo {
    return {
      owner: repo.owner.login,
      name: repo.name,
      url: repo.html_url,
      branch: repo.default_branch || 'main',
      description: repo.description || undefined,
      language: repo.language || undefined,
    };
  }

  /**
   * Helper: Build file tree from GitHub tree response
   */
  private buildFileTree(tree: any[]): FileTree {
    const root: FileTree = {
      name: '',
      type: 'directory',
      path: '',
      children: [],
    };

    const map = new Map<string, FileTree>();
    map.set('', root);

    // Sort by path to ensure parents are processed before children
    const sortedTree = tree.sort((a, b) => a.path.localeCompare(b.path));

    for (const item of sortedTree) {
      const parts = item.path.split('/');
      const name = parts[parts.length - 1];
      const parentPath = parts.slice(0, -1).join('/');

      const node: FileTree = {
        name,
        type: item.type === 'tree' ? 'directory' : 'file',
        path: item.path,
        size: item.size,
      };

      if (node.type === 'directory') {
        node.children = [];
      }

      const parent = map.get(parentPath) || root;
      if (parent.children) {
        parent.children.push(node);
      }

      map.set(item.path, node);
    }

    return root;
  }
}

/**
 * Parse GitHub repository URL
 */
export function parseGitHubUrl(url: string): { owner: string; repo: string } | null {
  const match = url.match(/github\.com[/:]([^/]+)\/([^/\.]+)/);
  if (!match) return null;

  return {
    owner: match[1],
    repo: match[2],
  };
}
