import { useState } from 'react';
import { useQuery, useMutation } from '@tanstack/react-query';
import { useAuth } from './useAuth';
import type { RepositoryInfo } from '@/types';

export function useGitHub() {
  const { user } = useAuth();
  const [error, setError] = useState<string | null>(null);

  // List repositories
  const { data: repositories, isLoading: isLoadingRepos, refetch: refetchRepos } = useQuery({
    queryKey: ['github', 'repositories', user?.id],
    queryFn: async () => {
      if (!user?.accessToken) throw new Error('Not authenticated');

      const response = await fetch('/api/github/repos', {
        headers: {
          'Authorization': `Bearer ${user.accessToken}`,
        },
      });

      if (!response.ok) {
        throw new Error('Failed to fetch repositories');
      }

      const data = await response.json();
      return data.data as RepositoryInfo[];
    },
    enabled: !!user?.accessToken,
    staleTime: 5 * 60 * 1000, // 5 minutes
  });

  // Get repository details
  const getRepository = async (owner: string, name: string): Promise<RepositoryInfo> => {
    if (!user?.accessToken) throw new Error('Not authenticated');

    const response = await fetch(`/api/github/repo/${owner}/${name}`, {
      headers: {
        'Authorization': `Bearer ${user.accessToken}`,
      },
    });

    if (!response.ok) {
      throw new Error('Failed to fetch repository');
    }

    const data = await response.json();
    return data.data;
  };

  // Create repository mutation
  const createRepositoryMutation = useMutation({
    mutationFn: async (params: {
      name: string;
      description?: string;
      isPrivate?: boolean;
    }) => {
      if (!user?.accessToken) throw new Error('Not authenticated');

      const response = await fetch('/api/github/repos', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${user.accessToken}`,
        },
        body: JSON.stringify(params),
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.error?.message || 'Failed to create repository');
      }

      const data = await response.json();
      return data.data as RepositoryInfo;
    },
    onSuccess: () => {
      refetchRepos();
    },
    onError: (error: Error) => {
      setError(error.message);
    },
  });

  // Get file tree
  const getFileTree = async (owner: string, repo: string, branch?: string) => {
    if (!user?.accessToken) throw new Error('Not authenticated');

    const params = new URLSearchParams();
    params.append('owner', owner);
    params.append('repo', repo);
    if (branch) params.append('branch', branch);

    const response = await fetch(`/api/github/tree?${params.toString()}`, {
      headers: {
        'Authorization': `Bearer ${user.accessToken}`,
      },
    });

    if (!response.ok) {
      throw new Error('Failed to fetch file tree');
    }

    const data = await response.json();
    return data.data;
  };

  // Create commit
  const createCommit = async (params: {
    owner: string;
    repo: string;
    branch: string;
    message: string;
    files: Array<{ path: string; content: string }>;
  }) => {
    if (!user?.accessToken) throw new Error('Not authenticated');

    const response = await fetch('/api/github/commit', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${user.accessToken}`,
      },
      body: JSON.stringify(params),
    });

    if (!response.ok) {
      const error = await response.json();
      throw new Error(error.error?.message || 'Failed to create commit');
    }

    const data = await response.json();
    return data.data;
  };

  return {
    repositories,
    isLoadingRepos,
    getRepository,
    createRepository: createRepositoryMutation.mutate,
    isCreatingRepo: createRepositoryMutation.isPending,
    getFileTree,
    createCommit,
    error,
    clearError: () => setError(null),
  };
}
