import { useState, useCallback } from 'react';
import { useAIAssistantStore } from '@/store';
import type { AIAssistantMessage } from '@/types';

export function useAIAssistant() {
  const { messages, isOpen, isProcessing, context, addMessage, setIsOpen, setIsProcessing, updateContext } = useAIAssistantStore();
  const [error, setError] = useState<string | null>(null);

  const sendMessage = useCallback(async (userMessage: string) => {
    // Add user message
    const userMsg: AIAssistantMessage = {
      id: `msg_${Date.now()}`,
      role: 'user',
      content: userMessage,
      timestamp: new Date(),
    };
    addMessage(userMsg);

    setIsProcessing(true);
    setError(null);

    try {
      const response = await fetch('/api/ai/chat', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          messages: [
            ...messages.map(m => ({ role: m.role, content: m.content })),
            { role: 'user', content: userMessage },
          ],
          context,
        }),
      });

      if (!response.ok) {
        throw new Error('Failed to get AI response');
      }

      // Handle streaming response
      const reader = response.body?.getReader();
      if (!reader) throw new Error('No response body');

      const decoder = new TextDecoder();
      let assistantContent = '';

      // Create assistant message placeholder
      const assistantMsg: AIAssistantMessage = {
        id: `msg_${Date.now() + 1}`,
        role: 'assistant',
        content: '',
        timestamp: new Date(),
      };
      addMessage(assistantMsg);

      while (true) {
        const { done, value } = await reader.read();
        if (done) break;

        const chunk = decoder.decode(value);
        const lines = chunk.split('\n').filter(line => line.trim() !== '');

        for (const line of lines) {
          if (line.startsWith('data: ')) {
            const data = line.slice(6);
            if (data === '[DONE]') continue;

            try {
              const parsed = JSON.parse(data);
              if (parsed.content) {
                assistantContent += parsed.content;
                // Update message in store
                assistantMsg.content = assistantContent;
              }
              if (parsed.suggestions) {
                assistantMsg.suggestions = parsed.suggestions;
              }
            } catch (e) {
              // Skip invalid JSON
            }
          }
        }
      }
    } catch (err: any) {
      setError(err.message);

      // Add error message
      const errorMsg: AIAssistantMessage = {
        id: `msg_${Date.now() + 2}`,
        role: 'assistant',
        content: 'I apologize, but I encountered an error. Please try again.',
        timestamp: new Date(),
      };
      addMessage(errorMsg);
    } finally {
      setIsProcessing(false);
    }
  }, [messages, context, addMessage, setIsProcessing]);

  const getSuggestion = useCallback(async (topic: string): Promise<string[]> => {
    try {
      const response = await fetch('/api/ai/chat', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          messages: [
            {
              role: 'system',
              content: 'You are a helpful assistant. Provide 3-5 short, actionable suggestions for the given topic.',
            },
            {
              role: 'user',
              content: `Give me suggestions for: ${topic}`,
            },
          ],
          maxTokens: 500,
        }),
      });

      const data = await response.json();
      const suggestions = data.data?.content
        ?.split('\n')
        .filter((s: string) => s.trim())
        .slice(0, 5);

      return suggestions || [];
    } catch (error) {
      console.error('Failed to get suggestions:', error);
      return [];
    }
  }, []);

  const open = useCallback(() => setIsOpen(true), [setIsOpen]);
  const close = useCallback(() => setIsOpen(false), [setIsOpen]);
  const toggle = useCallback(() => setIsOpen(!isOpen), [isOpen, setIsOpen]);

  return {
    messages,
    isOpen,
    isProcessing,
    error,
    sendMessage,
    getSuggestion,
    updateContext,
    open,
    close,
    toggle,
  };
}
