import { AIProviderFactory } from '@/lib/ai/factory';
import type { Message } from '@/lib/ai/factory';

export const runtime = 'edge';

export async function POST(request: NextRequest) {
  try {
    const body = await request.json();
    const { messages, model = 'claude-sonnet-4.5', temperature, maxTokens, apiKey, context } = body;

    if (!messages || !Array.isArray(messages)) {
      return new Response(
        JSON.stringify({
          error: { message: 'Messages array is required' },
        }),
        { status: 400 }
      );
    }

    // Determine which provider to use based on model
    let provider: any;
    let providerApiKey = apiKey;

    // If no API key provided, try environment variables
    if (!providerApiKey) {
      if (model.includes('gpt')) {
        providerApiKey = process.env.OPENAI_API_KEY;
      } else if (model.includes('claude')) {
        providerApiKey = process.env.ANTHROPIC_API_KEY;
      } else if (model.includes('gemini')) {
        providerApiKey = process.env.GOOGLE_AI_API_KEY;
      }
    }

    if (!providerApiKey) {
      return new Response(
        JSON.stringify({
          error: { message: 'API key is required' },
        }),
        { status: 400 }
      );
    }

    // Get provider
    if (model.includes('gpt')) {
      provider = AIProviderFactory.create('openai', providerApiKey);
    } else if (model.includes('claude')) {
      provider = AIProviderFactory.create('anthropic', providerApiKey);
    } else if (model.includes('gemini')) {
      provider = AIProviderFactory.create('google', providerApiKey);
    } else {
      return new Response(
        JSON.stringify({
          error: { message: 'Unsupported model' },
        }),
        { status: 400 }
      );
    }

    // Build system prompt with context if provided
    let systemPrompt = 'You are a helpful AI assistant for an AI orchestration platform. Help users set up and configure their AI agents.';
    if (context) {
      systemPrompt += `\n\nCurrent context:\n${JSON.stringify(context, null, 2)}`;
    }

    // Stream response
    const encoder = new TextEncoder();
    const stream = new ReadableStream({
      async start(controller) {
        try {
          const chatStream = provider.chatStream({
            messages: messages as Message[],
            model,
            temperature,
            maxTokens,
            systemPrompt,
          });

          for await (const chunk of chatStream) {
            const data = `data: ${JSON.stringify(chunk)}\n\n`;
            controller.enqueue(encoder.encode(data));
          }

          controller.enqueue(encoder.encode('data: [DONE]\n\n'));
          controller.close();
        } catch (error: any) {
          const errorData = `data: ${JSON.stringify({ error: error.message })}\n\n`;
          controller.enqueue(encoder.encode(errorData));
          controller.close();
        }
      },
    });

    return new Response(stream, {
      headers: {
        'Content-Type': 'text/event-stream',
        'Cache-Control': 'no-cache',
        'Connection': 'keep-alive',
      },
    });
  } catch (error: any) {
    return new Response(
      JSON.stringify({
        error: { message: error.message },
      }),
      { status: 500 }
    );
  }
}
