import { motion } from 'framer-motion';
import { Check } from 'lucide-react';
import { cn } from '@/lib/utils';

interface Step {
  id: number;
  name: string;
}

interface StepIndicatorProps {
  steps: Step[];
  currentStep: number;
  completedSteps: number[];
}

export function StepIndicator({ steps, currentStep, completedSteps }: StepIndicatorProps) {
  return (
    <nav aria-label="Progress" className="max-w-3xl mx-auto">
      <ol className="flex items-center justify-between">
        {steps.map((step, stepIdx) => {
          const isCompleted = completedSteps.includes(step.id);
          const isCurrent = step.id === currentStep;
          const isUpcoming = step.id > currentStep;

          return (
            <li
              key={step.id}
              className={cn(
                'relative flex-1',
                stepIdx !== steps.length - 1 && 'pr-8'
              )}
            >
              {/* Connector Line */}
              {stepIdx !== steps.length - 1 && (
                <div className="absolute top-4 left-8 right-0 h-0.5">
                  <div className={cn(
                    'h-full transition-colors duration-500',
                    isCompleted ? 'bg-cyan-600' : 'bg-gray-700'
                  )} />
                </div>
              )}

              {/* Step Circle */}
              <div className="relative flex flex-col items-center group">
                <motion.div
                  initial={false}
                  animate={{
                    scale: isCurrent ? 1.1 : 1,
                    backgroundColor: isCompleted
                      ? 'rgb(8, 145, 178)' // cyan-600
                      : isCurrent
                      ? 'rgb(17, 24, 39)' // gray-900
                      : 'rgb(31, 41, 55)', // gray-800
                  }}
                  className={cn(
                    'relative flex h-8 w-8 items-center justify-center rounded-full border-2 transition-colors',
                    isCompleted && 'border-cyan-600',
                    isCurrent && 'border-cyan-500',
                    isUpcoming && 'border-gray-600'
                  )}
                >
                  {isCompleted ? (
                    <motion.div
                      initial={{ scale: 0 }}
                      animate={{ scale: 1 }}
                      transition={{ type: 'spring', stiffness: 500, damping: 30 }}
                    >
                      <Check className="h-5 w-5 text-white" />
                    </motion.div>
                  ) : isCurrent ? (
                    <motion.span
                      animate={{ scale: [1, 1.2, 1] }}
                      transition={{ repeat: Infinity, duration: 2 }}
                      className="h-2.5 w-2.5 rounded-full bg-cyan-500"
                    />
                  ) : (
                    <span className="h-2 w-2 rounded-full bg-gray-600" />
                  )}
                </motion.div>

                {/* Step Label */}
                <span
                  className={cn(
                    'absolute -bottom-6 text-xs font-medium whitespace-nowrap transition-colors',
                    isCurrent && 'text-cyan-400',
                    isCompleted && 'text-cyan-500',
                    isUpcoming && 'text-gray-500'
                  )}
                >
                  {step.name}
                </span>
              </div>
            </li>
          );
        })}
      </ol>
    </nav>
  );
}
",
  "description": "Create Step Indicator component showing wizard progress with animations"
}


