import { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { FolderPlus, FolderGit2, Search, ArrowRight, ArrowLeft, Loader2 } from 'lucide-react';
import { useSetupWizardStore } from '@/store';
import { useGitHub } from '@/hooks/useGitHub';
import { Button } from '@/components/ui/Button';
import { Input } from '@/components/ui/Input';
import { cn } from '@/lib/utils';
import type { RepositoryInfo } from '@/types';

export function Step2Repository() {
  const { nextStep, prevStep, formData, updateFormData } = useSetupWizardStore();
  const { repositories, isLoadingRepos, createRepository, isCreatingRepo } = useGitHub();
  const [repoType, setRepoType] = useState<'new' | 'existing' | null>(formData.repositoryType);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedRepo, setSelectedRepo] = useState<RepositoryInfo | null>(null);

  // New repository fields
  const [newRepoName, setNewRepoName] = useState(formData.repositoryName || '');
  const [newRepoDescription, setNewRepoDescription] = useState(formData.repositoryDescription || '');
  const [errors, setErrors] = useState<Record<string, string>>({});

  const filteredRepos = repositories?.filter(repo =>
    repo.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
    repo.description?.toLowerCase().includes(searchQuery.toLowerCase())
  ) || [];

  const validateNewRepo = () => {
    const newErrors: Record<string, string> = {};
    if (!newRepoName) {
      newErrors.name = 'Repository name is required';
    } else if (!/^[a-z0-9-_]+$/i.test(newRepoName)) {
      newErrors.name = 'Only letters, numbers, hyphens, and underscores allowed';
    }
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleCreateRepo = async () => {
    if (!validateNewRepo()) return;

    createRepository({
      name: newRepoName,
      description: newRepoDescription,
      isPrivate: true,
    }, {
      onSuccess: (repo) => {
        updateFormData({
          repositoryType: 'new',
          repositoryName: repo.name,
          repositoryUrl: repo.url,
          repositoryDescription: repo.description,
        });
      },
    });
  };

  const handleSelectExisting = () => {
    if (!selectedRepo) return;

    updateFormData({
      repositoryType: 'existing',
      repositoryName: selectedRepo.name,
      repositoryUrl: selectedRepo.url,
      repositoryDescription: selectedRepo.description,
    });
  };

  const handleNext = () => {
    if (repoType === 'new' && !formData.repositoryName) {
      handleCreateRepo();
    } else if (repoType === 'existing' && !formData.repositoryUrl) {
      handleSelectExisting();
    } else {
      nextStep();
    }
  };

  const isNextDisabled = 
    !repoType ||
    (repoType === 'new' && (!newRepoName || isCreatingRepo)) ||
    (repoType === 'existing' && !selectedRepo);

  return (
    <div className="space-y-6">
      <div className="text-center mb-6">
        <h2 className="text-2xl font-semibold mb-2">Select Your Workspace</h2>
