import { useCallback, useState, useEffect } from 'react';
import ReactFlow, {
  Node,
  Edge,
  addEdge,
  Connection,
  useNodesState,
  useEdgesState,
  Controls,
  Background,
  MiniMap,
  BackgroundVariant,
  Panel,
} from 'reactflow';
import 'reactflow/dist/style.css';
import { motion } from 'framer-motion';
import { AgentNode } from './AgentNode';
import { ConditionNode } from './ConditionNode';
import { ParallelNode } from './ParallelNode';
import { LoopNode } from './LoopNode';
import { NodeLibrary } from './NodeLibrary';
import { WorkflowToolbar } from './WorkflowToolbar';
import { ConfigPanel } from './ConfigPanel';
import { ValidationPanel } from './ValidationPanel';
import type { Agent, WorkflowNode, WorkflowEdge } from '@/types';

const nodeTypes = {
  agent: AgentNode,
  condition: ConditionNode,
  parallel: ParallelNode,
  loop: LoopNode,
};

interface WorkflowBuilderProps {
  workflowId?: string;
  initialNodes?: WorkflowNode[];
  initialEdges?: WorkflowEdge[];
  onSave?: (nodes: Node[], edges: Edge[]) => void;
  readOnly?: boolean;
}

export function WorkflowBuilder({
  workflowId,
  initialNodes = [],
  initialEdges = [],
  onSave,
  readOnly = false,
}: WorkflowBuilderProps) {
  const [nodes, setNodes, onNodesChange] = useNodesState(initialNodes);
  const [edges, setEdges, onEdgesChange] = useEdgesState(initialEdges);
  const [selectedNode, setSelectedNode] = useState<Node | null>(null);
  const [showValidation, setShowValidation] = useState(false);
  const [isDirty, setIsDirty] = useState(false);

  // Mark as dirty when nodes or edges change
  useEffect(() => {
    if (nodes.length > 0 || edges.length > 0) {
      setIsDirty(true);
    }
  }, [nodes, edges]);

  const onConnect = useCallback(
    (connection: Connection) => {
      if (readOnly) return;

      setEdges((eds) =>
        addEdge(
          {
            ...connection,
            type: 'smoothstep',
            animated: true,
            style: { stroke: '#06b6d4', strokeWidth: 2 },
          },
          eds
        )
      );
    },
    [setEdges, readOnly]
  );

  const onNodeClick = useCallback((event: React.MouseEvent, node: Node) => {
    if (readOnly) return;
    setSelectedNode(node);
  }, [readOnly]);

  const onPaneClick = useCallback(() => {
    setSelectedNode(null);
  }, []);

  const onDragOver = useCallback((event: React.DragEvent) => {
    if (readOnly) return;
    event.preventDefault();
    event.dataTransfer.dropEffect = 'move';
  }, [readOnly]);

  const onDrop = useCallback(
    (event: React.DragEvent) => {
      if (readOnly) return;

      event.preventDefault();

      const type = event.dataTransfer.getData('application/reactflow');
      const agentData = event.dataTransfer.getData('agent');

      if (!type) return;

      const position = {
        x: event.clientX - 250,
        y: event.clientY - 100,
      };

      const newNode: Node = {
        id: `node_${Date.now()}`,
        type,
        position,
        data: agentData ? JSON.parse(agentData) : { label: `${type} node` },
      };

      setNodes((nds) => nds.concat(newNode));
    },
    [setNodes, readOnly]
  );

  const handleSave = useCallback(() => {
    if (onSave) {
      onSave(nodes, edges);
      setIsDirty(false);
    }
  }, [nodes, edges, onSave]);

  const handleValidate = useCallback(() => {
    setShowValidation(true);
  }, []);

  const handleDeleteNode = useCallback(() => {
    if (!selectedNode || readOnly) return;

    setNodes((nds) => nds.filter((n) => n.id !== selectedNode.id));
    setEdges((eds) => eds.filter((e) => e.source !== selectedNode.id && e.target !== selectedNode.id));
    setSelectedNode(null);
  }, [selectedNode, setNodes, setEdges, readOnly]);

  const handleUpdateNode = useCallback(
    (nodeId: string, data: any) => {
      setNodes((nds) =>
        nds.map((node) =>
          node.id === nodeId ? { ...node, data: { ...node.data, ...data } } : node
        )
      );
    },
    [setNodes]
  );

  return (
    <div className="h-full w-full relative">
      {/* Toolbar */}
      {!readOnly && (
        <WorkflowToolbar
          onSave={handleSave}
          onValidate={handleValidate}
          onUndo={() => {}}
          onRedo={() => {}}
          onZoomIn={() => {}}
          onZoomOut={() => {}}
          onFitView={() => {}}
          isDirty={isDirty}
        />
      )}

      <div className="flex h-full">
        {/* Node Library */}
        {!readOnly && (
          <NodeLibrary />
        )}

        {/* React Flow Canvas */}
        <div className="flex-1 relative">
          <ReactFlow
            nodes={nodes}
            edges={edges}
            onNodesChange={onNodesChange}
            onEdgesChange={onEdgesChange}
            onConnect={onConnect}
            onNodeClick={onNodeClick}
            onPaneClick={onPaneClick}
            onDrop={onDrop}
            onDragOver={onDragOver}
            nodeTypes={nodeTypes}
            fitView
            className="bg-gray-900"
            deleteKeyCode={readOnly ? null : ['Backspace', 'Delete']}
          >
            <Background
              variant={BackgroundVariant.Dots}
              gap={16}
              size={1}
              color="#374151"
            />
            <Controls className="glass rounded-lg" />
            <MiniMap
              className="glass rounded-lg"
              nodeColor={(node) => {
                switch (node.type) {
                  case 'agent':
                    return '#06b6d4';
                  case 'condition':
                    return '#f59e0b';
                  case 'parallel':
                    return '#8b5cf6';
                  case 'loop':
                    return '#10b981';
                  default:
                    return '#6b7280';
                }
              }}
            />

            {/* Empty State */}
            {nodes.length === 0 && (
              <Panel position="top-center">
                <motion.div
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  className="glass-strong rounded-lg p-6 text-center max-w-md"
                >
                  <h3 className="text-lg font-semibold mb-2">Start Building Your Workflow</h3>
                  <p className="text-gray-400 text-sm">
                    Drag agents from the library on the left to create your workflow
                  </p>
                </motion.div>
              </Panel>
            )}
          </ReactFlow>
        </div>

        {/* Configuration Panel */}
        {selectedNode && !readOnly && (
          <ConfigPanel
            node={selectedNode}
            onUpdate={handleUpdateNode}
            onDelete={handleDeleteNode}
            onClose={() => setSelectedNode(null)}
          />
        )}
      </div>

      {/* Validation Panel */}
      {showValidation && (
        <ValidationPanel
          nodes={nodes}
          edges={edges}
          onClose={() => setShowValidation(false)}
        />
      )}
    </div>
  );
}
",
  "description": "Create main WorkflowBuilder component using React Flow for visual workflow editing"
}


