function ValidationIssueItem({ issue }: { issue: ValidationIssue }) {
  const Icon = issue.type === 'error' ? AlertCircle :
               issue.type === 'warning' ? AlertTriangle : Info;

  const colors = {
    error: 'text-red-400 bg-red-500/10 border-red-500/50',
    warning: 'text-yellow-400 bg-yellow-500/10 border-yellow-500/50',
    info: 'text-cyan-400 bg-cyan-500/10 border-cyan-500/50',
  };

  return (
    <div className={cn('p-3 rounded-lg border-2', colors[issue.type])}>
      <div className="flex items-start gap-3">
        <Icon className="w-5 h-5 flex-shrink-0 mt-0.5" />
        <div className="flex-1">
          <p className="text-sm">{issue.message}</p>
          {issue.nodeId && (
            <p className="text-xs text-gray-500 mt-1">Node: {issue.nodeId}</p>
          )}
        </div>
      </div>
    </div>
  );
}

function validateWorkflow(nodes: Node[], edges: Edge[]): ValidationIssue[] {
  const issues: ValidationIssue[] = [];

  // Check if workflow has any nodes
  if (nodes.length === 0) {
    issues.push({
      type: 'error',
      message: 'Workflow is empty. Add at least one node to begin.',
    });
    return issues;
  }

  // Check for disconnected nodes
  const connectedNodeIds = new Set<string>();
  edges.forEach(edge => {
    connectedNodeIds.add(edge.source);
    connectedNodeIds.add(edge.target);
  });

  nodes.forEach(node => {
    if (nodes.length > 1 && !connectedNodeIds.has(node.id)) {
      issues.push({
        type: 'warning',
        message: `Node "${node.data.label || node.id}" is not connected to the workflow.`,
        nodeId: node.id,
      });
    }
  });

  // Check for cycles
  const hasCycle = detectCycle(nodes, edges);
  if (hasCycle) {
    issues.push({
      type: 'warning',
      message: 'Workflow contains a cycle. This may cause infinite loops.',
    });
  }

  // Check for start node
  const hasStartNode = nodes.some(node => {
    const incomingEdges = edges.filter(e => e.target === node.id);
    return incomingEdges.length === 0;
  });

  if (!hasStartNode && nodes.length > 0) {
    issues.push({
      type: 'error',
      message: 'Workflow must have at least one starting node (no incoming connections).',
    });
  }

  // Check for end node
  const hasEndNode = nodes.some(node => {
    const outgoingEdges = edges.filter(e => e.source === node.id);
    return outgoingEdges.length === 0;
  });

  if (!hasEndNode && nodes.length > 0) {
    issues.push({
      type: 'warning',
      message: 'Workflow should have at least one ending node (no outgoing connections).',
    });
  }

  // Info about workflow size
  if (nodes.length > 20) {
    issues.push({
      type: 'info',
      message: `Large workflow detected (${nodes.length} nodes). Consider breaking it into smaller workflows.`,
    });
  }

  return issues;
}

function detectCycle(nodes: Node[], edges: Edge[]): boolean {
  const visited = new Set<string>();
  const recursionStack = new Set<string>();

  function dfs(nodeId: string): boolean {
    visited.add(nodeId);
    recursionStack.add(nodeId);

    const outgoingEdges = edges.filter(e => e.source === nodeId);
    for (const edge of outgoingEdges) {
      if (!visited.has(edge.target)) {
        if (dfs(edge.target)) return true;
      } else if (recursionStack.has(edge.target)) {
        return true;
      }
    }

    recursionStack.delete(nodeId);
    return false;
  }

  for (const node of nodes) {
    if (!visited.has(node.id)) {
      if (dfs(node.id)) return true;
    }
  }

  return false;
}
