                    <span className="text-cyan-400">
                      ${calculateEstimatedCost(config).toFixed(4)}
                    </span>
                  </div>
                  <div className="flex justify-between">
                    <span>Provider:</span>
                    <span className="capitalize">{config.model.provider}</span>
                  </div>
                </div>
              </div>
            </div>
          </div>

          {/* Footer */}
          <div className="p-6 border-t border-gray-700 flex gap-3">
            <Button onClick={onClose} variant="outline" className="flex-1">
              Cancel
            </Button>
            <Button onClick={handleSave} className="flex-1 gap-2">
              <Save className="w-4 h-4" />
              Save Configuration
            </Button>
          </div>
        </motion.div>
      </motion.div>
    </AnimatePresence>
  );
}

function calculateEstimatedCost(config: AgentConfig): number {
  if (!config.model.costPerToken) return 0;

  const estimatedInputTokens = 1000; // Average
  const estimatedOutputTokens = config.maxTokens / 2; // Average

  return (
    estimatedInputTokens * config.model.costPerToken.input +
    estimatedOutputTokens * config.model.costPerToken.output
  );
}
